    male_data[''age_range''] = pd.Categorical(male_data[''age_range''], categories=distinct_age_ranges, ordered=True)
    female_data[''age_range''] = pd.Categorical(female_data[''age_range''], categories=distinct_age_ranges, ordered=True)

