# Function to fetch and process population data in the specified IPC phase
def get_ipc_population_data(country_code, ipc_level):
    query = f''''''
    SELECT f.adm1_code, f.population_in_phase AS population, s.geometry
    FROM hapi_food_food_security f
    JOIN hdx_shape_files s ON f.adm1_code = s.adm1_code
    WHERE f.adm0_code = ''{country_code}''
    AND f.adm1_code IS NOT NULL
    AND f.ipc_phase = ''{ipc_level}''
    AND f.latest = TRUE
    ''''''
    print(''Executing query:'', query)
    data = execute_query(query)
    print(''DEBUG INFO - Length of data is:'', len(data))
    return data

def get_attribution(country_code, ipc_level):
    query = f''''''
    SELECT distinct resource_hdx_id
    FROM hapi_food_food_security f
    WHERE f.adm0_code = ''{country_code}''
    AND f.adm1_code IS NOT NULL
    AND f.ipc_phase = ''{ipc_level}''
    AND f.latest = TRUE
    ''''''
    data = execute_query(query)
    if not data:
        print(''No data found for given parameters.'')
    resource_id = data[0][0]
    hdx_url = get_hdx_dataset_url(resource_id)

    return hdx_url

# Function to convert query results to GeoDataFrame
def convert_to_geodf(rows):
    df = pd.DataFrame(rows, columns=[''adm1_code'', ''population'', ''geometry''])
    if df.empty:
        raise ValueError(''No data found for given parameters.'')
    df[''geometry''] = df[''geometry''].apply(lambda x: wkb.loads(x, hex=True))
    gdf = gpd.GeoDataFrame(df, geometry=''geometry'')
    return gdf

# Function to plot population in IPC phase data on a map
def plot_ipc_population_map(gdf, country_code, ipc_level):
    fig, ax = plt.subplots(1, 1, figsize=(10, 15))
    gdf.plot(column=''population'', ax=ax, legend=False, cmap=''viridis'')

    cbar = plt.cm.ScalarMappable(norm=plt.Normalize(vmin=gdf[''population''].min(), vmax=gdf[''population''].max()), cmap=''viridis'')
    cbar = fig.colorbar(cbar, ax=ax, fraction=0.015, pad=0.04, label=''Population in Phase (millions)'')
    cbar.ax.set_yticklabels([f''{x/1e6:.1f}'' for x in cbar.get_ticks()])

    plt.title(f''Population in IPC Phase {ipc_level} by Admin 1 for {country_code}'')
    plt.xlabel(''Longitude'')
    plt.ylabel(''Latitude'')
    plt.savefig(''./work/ipc_population_map.png'')
    plt.close()
    return ''./work/ipc_population_map.png''

# Main function integrating all steps
def generate_ipc_population_map(country_code, ipc_level):
    try:
        data = get_ipc_population_data(country_code, ipc_level)
        gdf = convert_to_geodf(data)
        file_path = plot_ipc_population_map(gdf, country_code, ipc_level)
        attribution = get_attribution(country_code, ipc_level)
        return {''file'': file_path, ''attribution'': attribution}
    except Exception as e:
        return {''error'': str(e)}

