load_dotenv()

# Database connection details
DB_HOST = os.getenv("POSTGRES_DATA_HOST")
DB_PORT = os.getenv("POSTGRES_DATA_PORT")
DB_NAME = os.getenv("POSTGRES_DATA_DB")
DB_USER = os.getenv("POSTGRES_DATA_USER")
