# Create a database connection
engine = create_engine(f"postgresql://{DB_USER}:{DB_PASSWORD}@{DB_HOST}:{DB_PORT}/{DB_NAME}")

logging.basicConfig(level=logging.INFO)

def get_attribution(region):
    query = f"""
        SELECT distinct resource_hdx_id
        FROM hapi_coordination_context_operational_presence
        WHERE adm1_name = ''{region}''
    """

    data = execute_query(query)
    if not data:
        print(''No data found for given parameters.'')
    resource_id = data[0][0]
    hdx_url = get_hdx_dataset_url(resource_id)

    return hdx_url

def create_bar_chart_of_humanitarian_organizations_in_a_given_region_disaggregated_by_sector(region):
    # Query the database to get the data
    query = f"""
        SELECT sector_name, COUNT(''org_name'') as count
        FROM hapi_coordination_context_operational_presence
        WHERE adm1_name = ''{region}''
        GROUP BY sector_name
        ORDER BY count DESC
    """
    logging.info(f"Query: {query}")
    df = pd.read_sql(query, engine)
    print("DEBUG INFO - Dataframe shape:", df.shape)
    
    # Create the bar chart
    plt.figure(figsize=(10, 6))  # Ensure the figure is large enough
    plt.bar(df[''sector_name''], df[''count''])
    plt.xlabel(''Sector'')
    plt.ylabel(''Number of organizations'')
    plt.title(f''Humanitarian Organizations in {region} disaggregated by Sector'')
    plt.xticks(rotation=90)
    plt.tight_layout()  # Adjust layout to make room for labels

    # Create a bytes buffer for the plot and save it to the buffer
    buf = io.BytesIO()
    plt.savefig(buf, format=''png'', dpi=300)
    buf.seek(0)
    plt.close()

    # Encode the bytes buffer in base64
    image_base64 = base64.b64encode(buf.read()).decode(''utf-8'')
    image_base64 = image_base64.replace(''\n'', '''')

    image_file = f"./work/bar_chart_{region.replace('' '',''_'')}.png"
    with open(image_file, ''wb'') as f:
        f.write(base64.b64decode(image_base64))
    
    attribution = get_attribution(region)
    return {''file'': image_file, ''attribution'': attribution}

