def get_data_from_db(sql, params):
    """
    Connect to the PostgreSQL database and fetch data based on the provided SQL query and parameters.
    :param sql: SQL query string
    :param params: Parameters for the SQL query
    :return: List of rows fetched from the database
    """
    connection = psycopg2.connect(
        host=POSTGRES_DATA_HOST,
        port=POSTGRES_DATA_PORT,
        database=POSTGRES_DATA_DB,
        user=POSTGRES_DATA_USER,
        password=POSTGRES_DATA_PASSWORD
    )
    cursor = connection.cursor()
    cursor.execute(sql, params)
    rows = cursor.fetchall()
    cursor.close()
    connection.close()
    return rows


def get_country_name(adm0_code):
    """
    Fetch the country name based on the ISO3 country code (adm0_code).
    :param adm0_code: ISO3 country code
    :return: Country name
    """
    sql = ''SELECT DISTINCT name FROM hapi_metadata_location WHERE code = %s''
    rows = get_data_from_db(sql, [adm0_code])
    if rows:
        return rows[0][0]
    return ''Unknown Country''


def generate_population_map(adm0_code, admin_level):
    """
    Generate a population map for a given country code (adm0_code) and admin level (admin_level).
    :param adm0_code: ISO3 country code
    :param admin_level: Admin level to plot (''admin1'' or ''admin2'')
    :return: JSON string with the file path of the generated map
    """
    country_name = get_country_name(adm0_code)
    title = f''Population by {admin_level.capitalize()} for {country_name} ({adm0_code})''

    # Determine the admin level columns
    admin_code_col = ''adm1_code'' if admin_level == ''admin1'' else ''adm2_code''
    admin_name_col = ''adm1_name'' if admin_level == ''admin1'' else ''adm2_name''

    # SQL to get population data and geometry for the specified country
    sql = f''''''
        SELECT p.{admin_code_col}, p.population, s.geometry
        FROM hapi_population_social_population p
        JOIN hdx_shape_files s ON p.{admin_code_col} = s.{admin_code_col}
        WHERE p.adm0_code = %s AND p.latest = TRUE 
        AND gender = ''all'' 
        AND age_range = ''all'' 
        AND p.adm2_code IS NOT NULL
        AND p.adm1_code IS NOT NULL
    ''''''

    print(''DEBUG INFO - SQL Executed:'', sql)
    rows = get_data_from_db(sql, [adm0_code])
    print(''DEBUG INFO - Length of data is:'', len(rows))

    if len(rows) == 0:
        return json.dumps({''error'': ''No data found for the given country code.''})

    # Convert the data into a DataFrame
    df = pd.DataFrame(rows, columns=[admin_code_col, ''population'', ''geometry''])

    # Convert the population to millions
    df[''population''] = df[''population''] / 1_000_000

    # Convert the ''geometry'' column into a GeoSeries
    df[''geometry''] = df[''geometry''].apply(lambda x: wkb.loads(x, hex=True))

    # Convert the DataFrame into a GeoDataFrame
    gdf = gpd.GeoDataFrame(df, geometry=''geometry'')

    # Plot the map
    fig, ax = plt.subplots(1, 1, figsize=(12, 10))
    map = gdf.plot(column=''population'', ax=ax, legend=True, cmap=''OrRd'')

    # Get the colorbar
    colorbar = map.get_figure().get_axes()[1]

    # Set the label for the colorbar
    colorbar.set_ylabel(''Population (in millions)'')

    ax.set_title(title)
    ax.axis(''off'')

    # Save to file
    file_path = f''./work/population_map_{adm0_code}_{admin_level}.png''
    plt.savefig(file_path, bbox_inches=''tight'')
    plt.close()

    return json.dumps({''file'': file_path})

