DB_PASSWORD = os.getenv(''POSTGRES_DATA_PASSWORD'')


def get_db_connection():
    """
    Establish a connection to the PostgreSQL database.
    Returns:
        conn (psycopg2.extensions.connection): Database connection object.
    """
    try:
        conn = psycopg2.connect(
            host=DB_HOST,
            port=DB_PORT,
            dbname=DB_NAME,
            user=DB_USER,
            password=DB_PASSWORD
        )
        return conn
    except Exception as e:
        print(f''Error connecting to the database: {e}'')
        return None


def load_region_mapping():
    """
    Load region mapping from the hapi_metadata_admin1 table.
    Returns:
        dict: Dictionary with region names as keys and codes as values.
    """
    query = """
    SELECT name, code
    FROM hapi_metadata_admin1
    """
    conn = get_db_connection()
    if conn is None:
        return {}
    try:
        df = pd.read_sql_query(query, conn)
        return dict(zip(df[''name''], df[''code'']))
    except Exception as e:
        print(f''Error executing query: {e}'')
        return {}
    finally:
        conn.close()


REGION_MAPPING = load_region_mapping()


def find_tables_with_adm1_column():
    """
    Find tables that have a column named ''adm1_code''.
    Returns:
        list: List of table names that have the ''adm1_code'' column.
    """
    query = """
    SELECT table_name 
    FROM information_schema.columns 
    WHERE column_name = ''adm1_code''
    """
    conn = get_db_connection()
    if conn is None:
        return []
    try:
        df = pd.read_sql_query(query, conn)
        return df[''table_name''].tolist()
    except Exception as e:
        print(f''Error executing query: {e}'')
        return []
    finally:
        conn.close()


def check_data_for_subnational_region(table_name, adm1_code):
    """
    Check if a table has data for a specific subnational region.
    Args:
        table_name (str): Name of the table.
        adm1_code (str): Subnational region code.
    Returns:
        bool: True if data exists, False otherwise.
    """
    query = f"""
    SELECT 1 
    FROM {table_name} 
    WHERE adm1_code = %s 
    LIMIT 1
    """
    conn = get_db_connection()
    if conn is None:
        return False
    try:
        df = pd.read_sql_query(query, conn, params=(adm1_code,))
        return not df.empty
    except Exception as e:
        print(f''Error executing query: {e}'')
        return False
    finally:
        conn.close()


def summarize_tables_with_data(region):
    """
    Summarize which tables have data for a specific subnational region.
    Args:
        region (str): Subnational region name or code.
    Returns:
        str: Summary of tables with data.
    """
    adm1_code = REGION_MAPPING.get(region, region)  # Use the code if name is not found
    tables = find_tables_with_adm1_column()
    if not tables:
        return ''No tables found with the column adm1_code.''
    summary = ''Tables with data for subnational region (adm1_code: {}):\n\n''.format(adm1_code)
    for table in tables:
        if check_data_for_subnational_region(table, adm1_code):
            summary += f"- {table}\n"
    return summary if summary.strip() else ''No data found for the specified subnational region.''

