import os
import psycopg2
import pandas as pd
import matplotlib.pyplot as plt
from dotenv import load_dotenv
from sqlalchemy import create_engine

# Load environment variables from .env file
load_dotenv()

# Database connection details
DB_HOST = os.getenv(''POSTGRES_DATA_HOST'')
DB_PORT = os.getenv(''POSTGRES_DATA_PORT'')
DB_NAME = os.getenv(''POSTGRES_DATA_DB'')
DB_USER = os.getenv(''POSTGRES_DATA_USER'')
DB_PASSWORD = os.getenv(''POSTGRES_DATA_PASSWORD'')

# Create a SQLAlchemy engine
engine = create_engine(f''postgresql://{DB_USER}:{DB_PASSWORD}@{DB_HOST}:{DB_PORT}/{DB_NAME}'')


def get_country_name(country_code):
    """
    Fetch the country name for a given ISO3 country code.

    Args:
        country_code (str): The ISO3 country code.

    Returns:
        str: Country name.
    """
    query = f"""
    SELECT DISTINCT name
    FROM hapi_metadata_location
    WHERE code = ''{country_code}'';
    """
    print(''DEBUG INFO - SQL Query:'', query)

    # Execute the query and fetch the data
    df = pd.read_sql_query(query, engine)

    if df.empty:
        raise ValueError(''No country name found for the specified country code.'')

    print(''DEBUG INFO - Length of data is:'', len(df))
    return df[''name''].iloc[0]


def get_population_by_ipc_phase(country_code):
    """
    Fetch population by IPC phase for a specified country from the database.

    Args:
        country_code (str): The ISO3 country code.

    Returns:
        pd.DataFrame: DataFrame containing population by IPC phase.
    """
    query = f"""
    SELECT
        ipc_phase,
        SUM(population_in_phase) AS population
    FROM
        hapi_food_food_security
    WHERE
        adm0_code = ''{country_code}''
        AND adm1_code IS NOT NULL
        AND adm2_code IS NOT NULL
    GROUP BY
        ipc_phase
    ORDER BY
        population DESC;
    """
    print(''DEBUG INFO - SQL Query:'', query)

    # Execute the query and fetch the data
    df = pd.read_sql_query(query, engine)

    print(''DEBUG INFO - Length of data is:'', len(df))
    return df


def plot_population_by_ipc_phase(country_code):
    """
    Plot a bar chart of population by IPC phase for a specified country.

    Args:
        country_code (str): The ISO3 country code.

    Returns:
        str: File path to the saved plot image.
    """
    df = get_population_by_ipc_phase(country_code)
    country_name = get_country_name(country_code)

    if df.empty:
        raise ValueError(''No data found for the specified country code.'')

    # Plotting the bar chart
    plt.figure(figsize=(10, 6))
    plt.bar(df[''ipc_phase''], df[''population''], color=''skyblue'')
    plt.xlabel(''IPC Phase'')
    plt.ylabel(''Population'')
    plt.title(f''Population by IPC Phase for {country_name} ({country_code})'')
    plt.xticks(rotation=45)
    plt.tight_layout()

    # Save the plot as a PNG file
    file_path = f''./work/population_by_ipc_phase_{country_code}.png''
    plt.savefig(file_path)
    plt.close()

    return {''file'': file_path}

