    if not data:
        raise ValueError(''No data found for the given country code.'')
    df = pd.DataFrame(data, columns=[''reference_period_start'', ''total_fatalities''])
    return df


def get_commodity_prices(country_code, start_date):
    """
    Fetch commodity prices data for a given country starting from a specific date.

    Args:
        country_code (str): ISO3 country code.
        start_date (str): Start date in ''YYYY-MM-DD'' format.

    Returns:
        pd.DataFrame: DataFrame containing commodity prices data.
    """
    query = f"""
    SELECT 
        reference_period_start, 
        commodity_name, 
        price
    FROM 
        hapi_food_food_price
    WHERE 
        adm0_code = ''{country_code}''
        AND reference_period_start >= ''{start_date}''
    """
    data = execute_query(query)
    if not data:
        raise ValueError(''No data found for the given country code and date range.'')
    df = pd.DataFrame(data, columns=[''reference_period_start'', ''commodity_name'', ''price''])
    return df


def calculate_relative_average_monthly_movement(df):
    """
    Calculate the relative average monthly price movement of all commodities.

    Args:
        df (pd.DataFrame): DataFrame containing commodity prices data.

    Returns:
        pd.DataFrame: DataFrame containing average monthly price changes.
    """
    df[''reference_period_start''] = pd.to_datetime(df[''reference_period_start''])
    df[''month_year''] = df[''reference_period_start''].dt.to_period(''M'')
    df[''price_change''] = df.groupby(''commodity_name'')[''price''].pct_change() * 100
    monthly_avg_change = df.groupby(''month_year'')[''price_change''].mean().reset_index()
    return monthly_avg_change


def plot_price_vs_fatalities(country_code, start_date, output_path=''./work/price_vs_fatalities.png''):
    """
    Create and save a scatterplot showing price movements and fatality rates.

    Args:
        country_code (str): ISO3 country code.
        start_date (str): Start date in ''YYYY-MM-DD'' format.
        output_path (str): Path to save the output image.

    Returns:
        str: Path to the saved image.
    """
    fatalities_df = get_fatalities_data(country_code)
    prices_df = get_commodity_prices(country_code, start_date)
    monthly_avg_change = calculate_relative_average_monthly_movement(prices_df)

    fatalities_df[''reference_period_start''] = pd.to_datetime(fatalities_df[''reference_period_start''])
    fatalities_df[''month_year''] = fatalities_df[''reference_period_start''].dt.to_period(''M'')
    monthly_fatalities = fatalities_df.groupby(''month_year'')[''total_fatalities''].sum().reset_index()

    combined_df = pd.merge(monthly_avg_change, monthly_fatalities, on=''month_year'', how=''inner'')

    # TODO remove outliers
    combined_df = combined_df[combined_df[''total_fatalities''] < 100]

    plt.figure(figsize=(10, 6))
    sns.regplot(x=''total_fatalities'', y=''price_change'', data=combined_df, scatter_kws={''alpha'':0.5}, line_kws={"color": "blue"})
    plt.title(''Scatterplot of price movements of food items vs. fatality rates'')
    plt.ylabel(''Average monthly price change of food items (%)'')
    plt.xlabel(''Total fatalities'')
    plt.grid(True)
    plt.savefig(output_path)
    return output_path


