import json

# Load environment variables
load_dotenv()

# Function to get commodity prices data
def get_commodity_prices(country_code, start_date):
    query = f"""
    SELECT 
        reference_period_start, 
        commodity_name, 
        price
    FROM 
        hapi_food_food_price
    WHERE 
        adm0_code = ''{country_code}''
        AND reference_period_start >= ''{start_date}''
    """
    data = execute_query(query)
    print(pd.DataFrame(data).head(10))
    print(f"Query returned {len(data)} rows")
    return data
# Function to calculate relative average monthly price movement of all commodities
def calculate_relative_average_monthly_movement(df):
    # Extract month and year for grouping
    df[''reference_period_start''] = pd.to_datetime(df[''reference_period_start''])
    df[''month_year''] = df[''reference_period_start''].dt.to_period(''M'')
    
    # Calculate percentage change for each commodity per month
    df[''price_change''] = df.groupby(''commodity_name'')[''price''].pct_change() * 100
    
    # Calculate the average price change per month across all commodities
    monthly_avg_change = df.groupby(''month_year'')[''price_change''].mean().reset_index()
    
    return monthly_avg_change

# Adjusted function to plot commodity prices
def plot_commodity_prices(df, country_code):
    # Ensure ''month_year'' is converted to datetime for plotting
    df[''month_year_datetime''] = df[''month_year''].dt.to_timestamp()

    plt.figure(figsize=(12, 6))
    
    # Plotting the data
    plt.plot(df[''month_year_datetime''], df[''price_change''], 
             label=''Monthly Relative Change in Commodity Prices'', 
             linestyle=''-'', color=''b'')
    
    # Title and labels
    plt.title(f''Monthly Relative Change in Food Prices in {country_code}'')
    plt.xlabel(''Date'')
    plt.ylabel(''Monthly Relative Change (%)'')
    
    # Grid settings
    plt.grid(True, which=''both'', axis=''both'')
    
    # Setting x-ticks: generating a 6-month range based on min and max dates
    min_date = df[''month_year_datetime''].min()
    max_date = df[''month_year_datetime''].max()
    six_month_intervals = pd.date_range(start=min_date, end=max_date, freq=''6M'')
    
    # Convert to string for consistent x-ticks and plot
    plt.xticks(six_month_intervals.to_pydatetime(), rotation=90)
    
    # Layout and legend
    plt.tight_layout(rect=[0, 0, 1, 1])  # Adjust layout to shift x-axis labels
    plt.legend(loc=''upper left'', bbox_to_anchor=(1, 1))
    
    # Saving the plot
    output_file = ''./commodity_prices.png''
    plt.savefig(output_file)
    plt.close()
    
    return output_file

# Function to get attribution and data URL
def get_attribution_and_data_url(country_code, start_date):
    query = f"""
    SELECT DISTINCT resource_hdx_id
    FROM hapi_food_food_price
    WHERE adm0_code = ''{country_code}''
    AND reference_period_start >= ''{start_date}''
    AND adm1_code IS NULL
    AND adm2_code IS NULL
    """
    data = execute_query(query)
    if not data:
        return None, None
    resource_id = data[0][0]
    hdx_url, data_url = get_hdx_dataset_url(resource_id)
    return hdx_url, data_url

# Main function to get data and plot
def generate_commodity_price_plot(country_code, start_date):
    data = get_commodity_prices(country_code, start_date)
    if not data:
        print(''No data found for given parameters.'')
        return
    df = pd.DataFrame(data, columns=[''reference_period_start'', ''commodity_name'', ''price''])
    df[''reference_period_start''] = pd.to_datetime(df[''reference_period_start''])
    df.sort_values(by=''reference_period_start'', inplace=True)
    monthly_price_changes = calculate_relative_average_monthly_movement(df)
    plot_file = plot_commodity_prices(monthly_price_changes, country_code)
    hdx_url, data_url = get_attribution_and_data_url(country_code, start_date)
    result = {
        "result": {
            "type": "image",
            "file": f"{plot_file}",
            "value": ""
        },
        "metadata": {
            "params": {
                "country": f"{country_code}",
            },
            "parameters": {
                "country_code": f"{country_code}"
            },
            "attribution": f"{hdx_url}",
            "data_url": f"{data_url}",
            "time_period": {
                "start": f"{start_date}"
            }
        }
    }
    print("OUTPUT:")
    print(json.dumps(result, indent=4))
    return result

