def get_data_from_db(sql, params):
    """
    Connect to the PostgreSQL database and fetch data based on the provided SQL query and parameters.
    :param sql: SQL query string
    :param params: Parameters for the SQL query
    :return: List of rows fetched from the database
    """
    connection = psycopg2.connect(
        host=POSTGRES_DATA_HOST,
        port=POSTGRES_DATA_PORT,
        database=POSTGRES_DATA_DB,
        user=POSTGRES_DATA_USER,
        password=POSTGRES_DATA_PASSWORD
    )
    cursor = connection.cursor()
    cursor.execute(sql, params)
    rows = cursor.fetchall()
    cursor.close()
    connection.close()
    return rows


def get_country_name(adm0_code):
    """
    Fetch the country name based on the ISO3 country code (adm0_code).
    :param adm0_code: ISO3 country code
    :return: Country name
