    """
    sql = ''''''
        SELECT DISTINCT resource_hdx_id, reference_period_start, reference_period_end
        FROM hapi_population_social_population
        WHERE adm0_code = %s AND latest = TRUE
        AND gender = ''all''
        AND age_range = ''all''
        AND adm1_code IS NOT NULL
