def get_connection(params=DB_PARAMS):
    """
    Establishes a connection to the database using provided parameters.

    :param params: Dictionary containing database connection parameters.
    :return: psycopg2 connection object
    """
    conn = psycopg2.connect(**params)
    return conn


def execute_query(query, params=None):
    """
    Executes a SQL query and returns the results.

    :param query: SQL query to be executed.
    :param params: Parameters to be passed to the SQL query.
    :return: List of tuples containing the query results.
    """
    conn = get_connection()
    try:
        with conn.cursor() as cursor:
            cursor.execute(query, params)
            column_names = [desc[0] for desc in cursor.description]
            results = cursor.fetchall()
        df = pd.DataFrame(results, columns=column_names)
        print(f''SQL query executed. Number of records returned: {len(results)}'')
        return df
    except Exception as e:
        print(f''Error executing query: {e}'')
        return None
    finally:
        conn.close()


