from shapely import wkb
from dotenv import load_dotenv
import matplotlib.pyplot as plt
from skills import execute_query, get_hdx_dataset_url

# Load environment variables
load_dotenv()

# Function to fetch country code from metadata table
def get_country_code(country_name):
    query = f"""
    SELECT code
    FROM hapi_metadata_location
    WHERE name = ''{country_name}''
    """
    print("Executing query:", query)
    data = execute_query(query)
    if not data:
        raise ValueError("No country code found for the given country name.")
    return data[0][0]

# Function to fetch and process population data in the specified IPC phase
def get_ipc_population_data(country, ipc_level):
    country_code = get_country_code(country)
    query = f"""
    SELECT f.adm1_code, f.population_in_phase AS population, s.geometry
    FROM hapi_food_food_security f
    JOIN hdx_shape_files s ON f.adm1_code = s.adm1_code
    WHERE f.adm0_code = ''{country_code}''
    AND f.adm1_code IS NOT NULL
    AND f.ipc_phase = ''{ipc_level}''
    AND f.latest = TRUE
    """
    print("Executing query:", query)
    data = execute_query(query)
    print("DEBUG INFO - Length of data is:", len(data))
    return data

# Function to get attribution URL
def get_attribution(country, ipc_level):
    country_code = get_country_code(country)
    query = f"""
    SELECT distinct resource_hdx_id
    FROM hapi_food_food_security f
    WHERE f.adm0_code = ''{country_code}''
    AND f.adm1_code IS NOT NULL
    AND f.ipc_phase = ''{ipc_level}''
    AND f.latest = TRUE
    """
    data = execute_query(query)
    if not data:
        print("No data found for given parameters.")
    resource_id = data[0][0]
    hdx_url, resource_url = get_hdx_dataset_url(resource_id)
    return hdx_url, resource_url

# Function to get time range
def get_time_range(country, ipc_level):
    country_code = get_country_code(country)
    query = f"""
    SELECT distinct reference_period_start, reference_period_end
    FROM hapi_food_food_security f
    WHERE f.adm0_code = ''{country_code}''
    AND f.adm1_code IS NOT NULL
    AND f.ipc_phase = ''{ipc_level}''
    AND f.latest = TRUE
    """
    data = execute_query(query)
    if not data:
        print("No data found for given parameters.")
    start_period = data[0][0]
    end_period = data[0][1]

    return start_period, end_period

# Function to convert query results to GeoDataFrame
def convert_to_geodf(rows):
    df = pd.DataFrame(rows, columns=["adm1_code", "population", "geometry"])
    if df.empty:
        raise ValueError("No data found for given parameters.")
    df["geometry"] = df["geometry"].apply(lambda x: wkb.loads(x, hex=True))
    gdf = gpd.GeoDataFrame(df, geometry="geometry")
    return gdf

# Function to plot population in IPC phase data on a map
def plot_ipc_population_map(gdf, country, ipc_level):
    fig, ax = plt.subplots(1, 1, figsize=(10, 15))
    gdf.plot(column="population", ax=ax, legend=False, cmap="viridis")

    cbar = plt.cm.ScalarMappable(norm=plt.Normalize(vmin=gdf["population"].min(), vmax=gdf["population"].max()), cmap="viridis")
    cbar = fig.colorbar(cbar, ax=ax, fraction=0.015, pad=0.04, label="Population in Phase (millions)")
    cbar.ax.set_yticklabels([f"{x/1e6:.1f}" for x in cbar.get_ticks()])

    # Increase legend size by 30%
    cbar.ax.tick_params(labelsize=cbar.ax.get_yticklabels()[0].get_fontsize() * 1.3)

    plt.title(f"Population in IPC Phase {ipc_level} by Admin 1 for {country}", fontsize=20)
    plt.xlabel("Longitude")
    plt.ylabel("Latitude")
    plt.savefig("./work/ipc_population_map.png")
    plt.close()
    return "./work/ipc_population_map.png"

# Main function integrating all steps
def generate_ipc_population_map(country, ipc_level):
    data = get_ipc_population_data(country, ipc_level)
    gdf = convert_to_geodf(data)
    file_path = plot_ipc_population_map(gdf, country, ipc_level)
    attribution, data_url = get_attribution(country, ipc_level)
    start_period, end_period = get_time_range(country, ipc_level)

    result = {
        "result": {
            "type": "image",
            "file": file_path,
            "value": ""
        },
        "metadata": {
            "params": {
                "country": f"{country}",
                "ipc_level": f"{ipc_level}"
            },
            "attribution": f"{attribution}",
            "data_url": f"{data_url}",
            "time_period": {
                "start": f"{start_period}",
                "end": f"{end_period}"
            }
        }
    }
    print("OUTPUT:")
    print(json.dumps(result, indent=4))
    return result

