def get_fatalities_data(country_code):
    """
    Fetch fatalities data by month for a given country.

    Args:
        country_code (str): ISO3 country code.

    Returns:
        pd.DataFrame: DataFrame containing fatalities data.
    """
    query = f"""
    SELECT 
        reference_period_start, 
        SUM(fatalities) as total_fatalities
    FROM 
        hapi_coordination_context_conflict_event
    WHERE 
        adm0_code = ''{country_code}''
        AND reference_period_start >= ''2008-01-01''
    GROUP BY 
        reference_period_start
    ORDER BY 
        reference_period_start
    """
    data = execute_query(query)
    if not data:
        raise ValueError(''No data found for the given country code.'')
    df = pd.DataFrame(data, columns=[''reference_period_start'', ''total_fatalities''])
    return df


