# Function to calculate relative average monthly price movement of all commodities
def calculate_relative_average_monthly_movement(df):
    # Extract month and year for grouping
    df[''reference_period_start''] = pd.to_datetime(df[''reference_period_start''])
    df[''month_year''] = df[''reference_period_start''].dt.to_period(''M'')
    
    # Calculate percentage change for each commodity per month
    df[''price_change''] = df.groupby(''commodity_name'')[''price''].pct_change() * 100
    
    # Calculate the average price change per month across all commodities
    monthly_avg_change = df.groupby(''month_year'')[''price_change''].mean().reset_index()
    
    return monthly_avg_change

# Adjusted function to plot commodity prices
def plot_commodity_prices(df, country_code):
    # Ensure ''month_year'' is converted to datetime for plotting
    df[''month_year_datetime''] = df[''month_year''].dt.to_timestamp()

    plt.figure(figsize=(12, 6))
    
    # Plotting the data
    plt.plot(df[''month_year_datetime''], df[''price_change''], 
             label=''Monthly Relative Change in Commodity Prices'', 
             linestyle=''-'', color=''b'')
    
    # Title and labels
    plt.title(f''Monthly Relative Change in Food Prices in {country_code}'')
    plt.xlabel(''Date'')
    plt.ylabel(''Monthly Relative Change (%)'')
    
    # Grid settings
    plt.grid(True, which=''both'', axis=''both'')
    
    # Setting x-ticks: generating a 6-month range based on min and max dates
    min_date = df[''month_year_datetime''].min()
    max_date = df[''month_year_datetime''].max()
