def get_events_data(country_code):
    """
    Fetch events data by month for a given country.

    Args:
        country_code (str): ISO3 country code.

    Returns:
        pd.DataFrame: DataFrame containing events data.
    """
    query = f"""
    SELECT 
        reference_period_start, 
        SUM(events) as total_events
    FROM 
        hapi_coordination_context_conflict_event
    WHERE 
        adm0_code = ''{country_code}''
        AND reference_period_start >= ''2008-01-01''
    GROUP BY 
        reference_period_start
    ORDER BY 
        reference_period_start
    """
    data = execute_query(query)
    if not data:
        raise ValueError(''No data found for the given country code.'')
    df = pd.DataFrame(data, columns=[''reference_period_start'', ''total_events''])
    return df


def plot_events_by_month(country_code, output_path=''./work/events_by_month.png''):
    """
    Plot a line chart of events by month for a given country.

    Args:
        country_code (str): ISO3 country code.
        output_path (str): Path to save the output image.

    Returns:
        str: Path to the saved image.
    """
    df = get_events_data(country_code)

    # Convert reference_period_start to datetime
    df[''reference_period_start''] = pd.to_datetime(df[''reference_period_start''])

    # Format the x-axis labels to show only yyyy-mm
    df[''reference_period_start''] = df[''reference_period_start''].dt.to_period(''M'').dt.to_timestamp()

    # Plotting
    plt.figure(figsize=(12, 8))
    plt.plot(df[''reference_period_start''], df[''total_events''], linestyle=''-'')
    plt.title(f''events by Month for Chad'')
    plt.xlabel(''Month'')
    plt.ylabel(''Total events'')
    plt.grid(True, which=''both'', axis=''both'')
    plt.xticks(pd.date_range(start=''2008-01-01'', end=df[''reference_period_start''].max(), freq=''6M'').to_period(''M'').to_timestamp(), rotation=90)
    plt.tight_layout(rect=[0, 0, 1, 1])  # Adjust layout to shift x-axis labels
    plt.savefig(output_path)
    plt.close()

    return output_path


def get_attribution(country_code):
    """
    Get the attribution URL for a given country.

    Args:
        country_code (str): ISO3 country code.

    Returns:
        dict: Dictionary containing attribution and data URLs.
    """
    query = f"""
    SELECT DISTINCT resource_hdx_id
    FROM hapi_coordination_context_conflict_event
    WHERE adm0_code = ''{country_code}''
    """
    data = execute_query(query)
    if not data:
        raise ValueError(''No attribution data found for the given country code.'')
    resource_id = data[0][0]
    hdx_url, data_url = get_hdx_dataset_url(resource_id)
    return {''hdx_url'': hdx_url, ''data_url'': data_url}


def generate_events_report(country_code):
    """
    Generate a report of events by month for a given country.

    Args:
        country_code (str): ISO3 country code.

    Returns:
        dict: JSON object containing the report.
    """
    try:
        image_path = plot_events_by_month(country_code)
        attribution = get_attribution(country_code)
        result = {
            "result": {
                "type": "image",
                "file": f"{image_path}"
            },
            "metadata": {
                "params": {
                    "country_code": f"{country_code}"
                },
                "attribution": f"{attribution[''hdx_url'']}",
