# Function to get commodity prices for a country and commodity
def get_commodity_prices(country_code, commodity_name, reference_period_start):
    query = f"""
    SELECT reference_period_start, currency_code, AVG(price) as mean_price
    FROM hapi_food_food_price
    WHERE adm0_code = ''{country_code}''
    AND commodity_name = ''{commodity_name}''
    AND reference_period_start >= ''{reference_period_start}''
    GROUP BY reference_period_start, currency_code
    """
    print(f''SQL Query: {query}'')
    data = execute_query(query)
    print(f''DEBUG INFO - Length of data is: {len(data)}'')
    return data

# Function to plot commodity prices
def plot_commodity_prices(data, country_code, commodity_name):
    df = pd.DataFrame(data, columns=[''reference_period_start'', ''currency_code'', ''mean_price''])
    if df.empty:
        print(''No data available for the given country code.'')
        return None

    # Truncate the last 3 characters of the x-axis labels
    df[''reference_period_start''] = pd.to_datetime(df[''reference_period_start''].apply(lambda x: x[:-3]))

    # Plotting
    plt.figure(figsize=(10, 6))
    plt.plot(df[''reference_period_start''], df[''mean_price''], marker=''o'')

    plt.title(f''Average {commodity_name.lower()} prices in {country_code}'')
    plt.xlabel(''Timeframe'')
    plt.ylabel(f"Average price per kg ({df[''currency_code''].iloc[0]})")
    plt.xticks(rotation=45)
    plt.grid(True)

    # Set major ticks every 6 months
    plt.gca().xaxis.set_major_locator(mdates.MonthLocator(interval=6))
    plt.gca().xaxis.set_major_formatter(mdates.DateFormatter(''%Y-%m''))

    # Add grid lines for each month
    plt.gca().xaxis.set_minor_locator(mdates.MonthLocator())
    plt.gca().xaxis.grid(True, which=''minor'', linestyle=''--'', linewidth=0.5)

    # Save plot to file
    file_path = f''./work/commodity_prices_{country_code}.png''
    plt.savefig(file_path)
    plt.close()
    return file_path

