def get_food_security_data(country_code, ipc_phase):
    """
    Fetch food security data for a given country and IPC phase.

    Args:
        country_code (str): The ISO3 code of the country.
        ipc_phase (str): The IPC phase to filter by.

    Returns:
        pd.DataFrame: DataFrame containing food security data.
    """
    query = f"""
    SELECT adm1_code, adm1_name, SUM(population_in_phase) as population_in_phase
    FROM hapi_food_food_security
    WHERE adm0_code = ''{country_code}''
    AND ipc_phase = ''{ipc_phase}''
    AND latest = TRUE
    GROUP BY adm1_code, adm1_name
    ORDER BY population_in_phase DESC
    LIMIT 3
    """
    data = execute_query(query)
    return pd.DataFrame(data, columns=[''adm1_code'', ''adm1_name'', ''population_in_phase''])


