def get_population_data_by_age(adm0_code):
    query = f''''''
    SELECT p.age_range, p.gender, SUM(p.population) as population
    FROM hapi_population_social_population p
    WHERE p.adm0_code = ''{adm0_code}''
    AND p.adm1_code is not null
    AND p.adm2_code is not null
    AND p.age_range != ''all''
    GROUP BY p.age_range, p.gender
    ''''''

    print(f''DEBUG: SQL Query - {query}'')
    data = skills.execute_query(query)
    print(''DEBUG INFO - Length of data is:'', len(data))

    if len(data) == 0:
        return pd.DataFrame()

    return pd.DataFrame(data, columns=[''age_range'', ''gender'', ''population''])

# Function to get distinct age ranges
def get_distinct_age_ranges(adm0_code):
    query = f''''''
    SELECT DISTINCT age_range
    FROM hapi_population_social_population p
    WHERE p.adm0_code = ''{adm0_code}''
    AND p.adm1_code is not null
    AND p.adm2_code is not null
    AND p.age_range != ''all''
    ''''''

    print(f''DEBUG: SQL Query - {query}'')
    data = skills.execute_query(query)
    print(''DEBUG INFO - Length of data is:'', len(data))

    age_ranges = [row[0] for row in data]  # Extract age ranges

    def age_range_key(age_range):
        if age_range == ''all'':
            return float(''inf'')
        elif age_range.endswith(''+''):
            return int(age_range[:-1])
        else:
            return int(age_range.split(''-'')[0])

    age_ranges.sort(key=age_range_key)
    print(''DEBUG INFO - Sorted age ranges:'', age_ranges)

    return age_ranges

