def get_fatalities_and_maize_prices_data(country_code):
    """
    Retrieve fatalities and average maize (white) prices data for a given country.

    Args:
        country_code (str): ISO3 country code.

    Returns:
        pd.DataFrame: DataFrame containing fatalities and maize prices data.
    """
    query = f"""
    -- Aggregate conflict events by adm0_code and year
    WITH aggregated_conflict AS (
    SELECT 
        adm0_code,
        EXTRACT(YEAR FROM CAST(reference_period_start AS DATE)) AS year,
        SUM(fatalities) AS total_fatalities
    FROM 
        hapi_coordination_context_conflict_event
    WHERE 
        adm0_code = ''{country_code}''
    GROUP BY 
        adm0_code, 
        EXTRACT(YEAR FROM CAST(reference_period_start AS DATE))
    ),

    -- Aggregate maize prices by adm0_code and year
    aggregated_maize_prices AS (
        SELECT 
            adm0_code,
            EXTRACT(YEAR FROM CAST(reference_period_start AS DATE)) AS year,
            AVG(price) AS avg_maize_price
        FROM 
            hapi_food_food_price
        WHERE 
            adm0_code = ''{country_code}''
            AND commodity_name = ''Maize (white)''
        GROUP BY 
            adm0_code, EXTRACT(YEAR FROM CAST(reference_period_start AS DATE))
    )

    -- Join the aggregated results on adm0_code and year
    SELECT 
        ac.adm0_code,
        ac.year,
        ac.total_fatalities,
        amp.avg_maize_price
    FROM 
        aggregated_conflict ac
    JOIN 
        aggregated_maize_prices amp
    ON 
        ac.adm0_code = amp.adm0_code
        AND ac.year = amp.year
    """
    print(''Executing query:'', query)
    data = execute_query(query)
    print(''DEBUG INFO - Length of data is:'', len(data))
    if not data:
        return pd.DataFrame()
    df = pd.DataFrame(data, columns=[''adm0_code'', ''year'', ''fatalities'', ''maize_price''])
    return df


def plot_scatterplot(df, output_file):
    """
    Plot a scatterplot of fatalities vs. maize prices for a given country, including a regression line.

    Args:
        df (pd.DataFrame): DataFrame containing fatalities and maize prices data.
        output_file (str): Path to save the output image.

    Returns:
        dict: JSON record with file path and metadata.
    """
    # Ensure the relevant columns are of numeric type
    df[''fatalities''] = pd.to_numeric(df[''fatalities''], errors=''coerce'')
    df[''maize_price''] = pd.to_numeric(df[''maize_price''], errors=''coerce'')

    # Drop any rows with NaN values after conversion
    df = df.dropna(subset=[''fatalities'', ''maize_price''])

