# Function to fetch and process population data in the specified IPC phase
def get_ipc_population_data(country, ipc_level):
    country_code = get_country_code(country)
    query = f"""
    SELECT f.adm1_code, f.population_in_phase AS population, s.geometry
    FROM hapi_food_food_security f
    JOIN hdx_shape_files s ON f.adm1_code = s.adm1_code
    WHERE f.adm0_code = ''{country_code}''
    AND f.adm1_code IS NOT NULL
    AND f.ipc_phase = ''{ipc_level}''
