# Function to convert query results to GeoDataFrame
def convert_to_geodf(rows):
    df = pd.DataFrame(rows, columns=["adm1_code", "population", "geometry"])
    if df.empty:
        raise ValueError("No data found for given parameters.")
    df["geometry"] = df["geometry"].apply(lambda x: wkb.loads(x, hex=True))
    gdf = gpd.GeoDataFrame(df, geometry="geometry")
    return gdf

