DB_NAME = os.getenv(''POSTGRES_DATA_DB'')
DB_USER = os.getenv(''POSTGRES_DATA_USER'')
DB_PASSWORD = os.getenv(''POSTGRES_DATA_PASSWORD'')


def get_db_connection():
    """
    Establish a connection to the PostgreSQL database.
    Returns:
        conn: psycopg2 connection object
    """
    try:
        conn = psycopg2.connect(
            host=DB_HOST,
            port=DB_PORT,
            dbname=DB_NAME,
            user=DB_USER,
            password=DB_PASSWORD
        )
        return conn
    except Exception as e:
        print(f''Error connecting to the database: {e}'')
        return None


def get_total_population(country_code):
    """
    Retrieve the total population for a given country using HDX data.
    Args:
        country_code (str): The ISO3 code of the country.
    Returns:
        int: Total population of the country.
    """
    query = f''''''
    SELECT SUM(population) AS total_population
    FROM hapi_population_social_population
    WHERE adm0_code = ''{country_code}''
      AND gender = ''all''
      AND age_range = ''all''
      AND adm1_code IS NULL
      AND adm2_code IS NULL
      AND latest = TRUE;
    ''''''
    print(''Executing SQL query:'')
    print(query)

    conn = get_db_connection()
    if conn is None:
        return None

    try:
        df = pd.read_sql_query(query, conn)
        print(f''DEBUG INFO - Length of data is: {len(df)}'')
        if not df.empty:
            total_population = df.iloc[0][''total_population'']
            return int(total_population) if total_population is not None else 0
        else:
            return 0
    except Exception as e:
        print(f''Error executing query: {e}'')
        return None
    finally:
        conn.close()


